import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import InputData from "./InputData";
import { AppMode, RunMode } from "../HomePage";
import "./SmartFilterLayout.css";

type SmartFilterLayoutProps = {
  mode: AppMode;
  setMode: React.Dispatch<React.SetStateAction<AppMode>>;
  runmode: RunMode;
  setRunmode: React.Dispatch<React.SetStateAction<RunMode>>;
  onSubmit: (inputData: any) => void;
  setBatch: React.Dispatch<React.SetStateAction<boolean>>;
  setView: React.Dispatch<React.SetStateAction<boolean>>;
  setPainsChecked: React.Dispatch<React.SetStateAction<boolean>>;
  view: boolean;
  children: React.ReactNode;
};

const SmartFilterLayout: React.FC<SmartFilterLayoutProps> = ({
  mode,
  setMode,
  onSubmit,
  setBatch,
  setView,
  view,
  children,
}) => {
  const [delimiter, setDelimiter] = useState<string>("' '");
  const [smileCol, setSmileCol] = useState<number>(0);
  const [nameCol, setNameCol] = useState<number>(1);

  // Dark mode state with persistence in localStorage
  const [darkMode, setDarkMode] = useState<boolean>(() => {
    if (typeof window !== "undefined") {
      return localStorage.getItem("darkMode") === "true";
    }
    return false;
  });

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode.toString());
  }, [darkMode]);

  // CSS classes for mode + dark mode
  const layoutClass = `layout-container ${
    mode === "expert" ? "mode-expert" : "mode-normal"
  } ${darkMode ? "dark-mode" : "light-mode"}`;

  return (
    <div className={layoutClass}>
      {/* Top Bar */}
      <div className="row align-items-center mb-4 border-bottom pb-2">
        <div className="col-md-3 text-start">
          <div className="form-check form-switch">
            <input
              className="form-check-input"
              type="checkbox"
              id="modeSwitch"
              checked={mode === "expert"}
              onChange={(e) => setMode(e.target.checked ? "expert" : "normal")}
            />
            <label className="form-check-label" htmlFor="modeSwitch">
              {mode === "expert" ? "Expert" : "Normal"}
            </label>
          </div>
        </div>
        <div className="col-md-6 text-center">
          <span className="fs-4 fw-bold">SmartFilter</span>
        </div>
        <div className="col-md-3 text-end d-flex justify-content-end align-items-center gap-3">
          <button className="btn btn-outline-secondary btn-sm">Help</button>
          <button className="btn btn-outline-info btn-sm">Demo</button>
          <button className="btn btn-outline-danger btn-sm">Reset</button>

          {/* Dark Mode Toggle */}
          <div className="form-check form-switch mb-0">
            <input
              className="form-check-input"
              type="checkbox"
              id="darkModeSwitch"
              checked={darkMode}
              onChange={(e) => setDarkMode(e.target.checked)}
            />
            <label
              className="form-check-label text-nowrap"
              htmlFor="darkModeSwitch"
              style={{ userSelect: "none" }}
            >
              Dark Mode
            </label>
          </div>
        </div>
      </div>

      {/* Input and Config */}
      <div className="row g-4 mb-4">
        <div className="col-md-8">
          <div className="card shadow-sm h-100">
            <div className="card-header bg-primary text-white">Input Data</div>
            <div className="card-body">
              <InputData
                onSubmit={onSubmit}
                showSmarts={mode === "expert"}
                delimiter={delimiter}
                smileCol={smileCol}
                nameCol={nameCol}
              />
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="card shadow-sm h-100">
            <div className="card-header bg-dark text-white">Configuration</div>
            <div className="card-body">
              <div className="row">
                <div className="col-6">
                  <h6>Input</h6>
                  <div className="mb-2">
                    <label className="form-label">Delimiter</label>
                    <input
                      type="text"
                      className="form-control form-control-sm"
                      placeholder="' ' or ','"
                      value={delimiter}
                      onChange={(e) => setDelimiter(e.target.value)}
                    />
                  </div>
                  <div className="mb-2">
                    <label className="form-label">SMILES Col</label>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={smileCol}
                      onChange={(e) => setSmileCol(Number(e.target.value))}
                    />
                  </div>
                  <div className="mb-2">
                    <label className="form-label">Name Col</label>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={nameCol}
                      onChange={(e) => setNameCol(Number(e.target.value))}
                    />
                  </div>
                </div>

                <div className="col-1 border-start"></div>

                <div className="col-5">
                  <h6>Output</h6>
                  {/* Add your output checkboxes here */}
                  <div className="form-check mb-1">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      id="batchCheck"
                      onChange={(e) => setBatch(e.target.checked)}
                    />
                    <label className="form-check-label" htmlFor="batchCheck">
                      Batch
                    </label>
                  </div>
                  <div className="form-check mb-1">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      id="viewCheck"
                      checked={view}
                      onChange={(e) => setView(e.target.checked)}
                    />
                    <label className="form-check-label" htmlFor="viewCheck">
                      View
                    </label>
                  </div>

                  {mode === "expert" && (
                    <>
                      <div className="form-check mb-1">
                        <input
                          className="form-check-input"
                          type="checkbox"
                          id="showMatch"
                        />
                        <label
                          className="form-check-label"
                          htmlFor="showMatch"
                        >
                          Show Matches
                        </label>
                      </div>
                      {/* Add other expert options here */}
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {children}
    </div>
  );
};

export default SmartFilterLayout;
